var path = require('path');

var racine = path.join(__dirname, '../');

function noms(racine){
	// fichier d'appel a certeurope
	this.certiclic = path.join(racine + 'app_proposition/action_utilisateur/action_avec_Certeurope/Certiclic.js');
	this.certisms = path.join(racine + 'app_proposition/action_utilisateur/action_avec_Certeurope/Certisms.js');
	this.certiserveur = path.join(racine + 'app_proposition/action_utilisateur/action_avec_Certeurope/Certiserveur.js');
	
	//modele
	this.models =  path.join(racine + 'app_proposition/action_utilisateur/models');
	this.campain = path.join(this.models, 'campain.js');
	this.employeur = path.join(this.models, 'employeur.js');
	this.log = path.join(this.models, 'log.js');
	
	//modele compete (etape action utilisateur)
	this.interaction_utilisateur = path.join(racine + 'app_proposition/action_utilisateur/interaction_utilisateur.js')
	this.interaction_employeur = path.join(racine + 'app_proposition/action_utilisateur/interaction_employeur.js')
	
	//parametres secrets
	this.parametres = "C:/Users/parametres.js"
}

noms.prototype.ajouter_fichier_local = function(cle, emplacement_local, emplacement_fichier_en_local){
	this[cle] = path.join(emplacement_local, emplacement_fichier_en_local);
}

var emplacement_fichiers = new noms(racine);
module.exports = emplacement_fichiers;